$(function(){function submitForm(){var name=$("#name").val(),email=$("#email").val(),message=$("#message").val();$.ajax({type:"POST",url:"php/contact.php",data:"name="+name+"&email="+email+"&message="+message,success:function(text){"success"==text?formSuccess():(formError(),submitMSG(!1,text))}})}function formSuccess(){$("#contactForm")[0].reset(),submitMSG(!0,"Message Submitted!")}function formError(){$("#contactForm").removeClass().addClass("shake animated").one("webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",function(){$(this).removeClass()})}function submitMSG(valid,msg){var msgClasses;msgClasses=valid?"h3 text-center text-success":"h3 text-center text-danger",$("#msgSubmit").removeClass().addClass(msgClasses).text(msg)}$("#contactForm").validator().on("submit",function(event){event.isDefaultPrevented()?(formError(),submitMSG(!1,"Did you fill in the form properly?")):(event.preventDefault(),submitForm())})});